import React, { useState } from 'react';

interface InputProps {
    placeholder: string;
    type: string;
    error?: string; 
}

const InputModel: React.FC<InputProps> = ({ placeholder, type, error }) => {
    const [isErrorVisible, setIsErrorVisible] = useState<boolean>(false);

    const handleFocus = () => {
        setIsErrorVisible(false); // Oculta a div de erro quando o campo recebe foco
    };

    const handleBlur = () => {
        setIsErrorVisible(!!error); // Exibe a div de erro apenas se a propriedade error estiver presente
    };

    return (
        <div className="my-10">
            <input
                placeholder={placeholder}
                type={type}
                className="p-5 w-full bg-slate-100 rounded-xl placeholder:text-lg"
                onFocus={handleFocus}
                onBlur={handleBlur}
            />
            {isErrorVisible && error && <div className="error">{error}</div>} 
        </div>
    );
};

export default InputModel;
