import { useForm } from "react-hook-form";
import FormModel from "../FormModel/FormModel";
import InputModel from "../Input/Input";

interface FormProps {
  buttonText: string;
}

const FormRegister: React.FC<FormProps> = ({ buttonText }) => {
  const { register, handleSubmit, formState: { errors }, setError, getValues } = useForm();

  const submitForm = (data: any) => {
    // Verifique se o checkbox foi marcado
    if (!getValues("agree")) {
      // Se não foi marcado, defina um erro para o checkbox
      setError("agree", {
        type: "required",
        message: "Você precisa concordar com os termos de serviço e privacidade."
      });
      return;
    }
    // Se o checkbox foi marcado, continue com o envio do formulário
    window.location.reload();
  };

  return (
    <FormModel onSubmit={handleSubmit(submitForm)} buttonText={buttonText}>
      <InputModel
        register={register}
        type="email"
        placeholder="Digite seu email"
        error={errors.email && errors.email.message}
      />
      
      <InputModel
        register={register}
        type="tel"
        placeholder="Celular"
        error={errors.tel && errors.tel.message}
      />

      <InputModel
        register={register}
        type="text"
        placeholder="Nome ou apelido"
        error={errors.text && errors.text.message}
      />

      <div className="flex gap-2 items-center my-3">
        <input
          className="checkbox: h-3/4"
          type="checkbox"
          {...register("agree", { required: true })} // Adicione regras de validação aqui
        />
        <label className="text-gray-400 text-2xl">
          I agree with 
          <a href="#" className="text-gray-800 hover:underline"> terms of service & privacy</a>
        </label>
        {/* Mostre o erro do checkbox se houver */}
        {errors.agree && <span className="text-red-500">{errors.agree.message}</span>}
      </div>
    </FormModel>
  );
};

export default FormRegister;
