import { useForm } from "react-hook-form";
import FormModel from "../FormModel/FormModel";
import InputModel from "../Input/Input";

interface FormProps {
  buttonText: string;
}

const FormRegister: React.FC<FormProps> = ({ buttonText }) => {
  const { register,  handleSubmit, formState: { errors }, } = useForm();

  if (!getValues("agree")) {
    // Se não foi marcado, defina um erro para o checkbox
    setError("agree", {
      type: "required",
      message: "Você precisa concordar com os termos de serviço e privacidade."
    });
    return;
  }
  // Se o checkbox foi marcado, continue com o envio do formulário
  window.location.reload();

  return (

    <>
      <FormModel onSubmit={handleSubmit(submitForm)} buttonText={buttonText}>

        <InputModel
          register={register}
          type="email"
          placeholder="Digite seu email"
          error={errors.email && "Insira uma valor válido para Email!"}
        />
        
        <InputModel
          register={register}
          type="tel"
          placeholder="Celular"
          error={errors.tel && "Insira uma valor válido para Celular!"}
        />

        <InputModel
          register={register}
          type="text"
          placeholder="Nome ou apelido"
          error={errors.text && "Insira um nome ou apelido válido!"}
        />

        <div className="flex gap-2 items-center my-3">

         <input 
         style={{ width: '1.5em', height: '1.5em' }}
         className="checkbox: h-3/4" type="checkbox" 
         {...register("agree", { required: true })} 
          />

         <label className="text-gray-400 text-2xl">I agree with 
         <a href="#" className="text-gray-800 hover:underline"> terms of service & privacy</a></label>

         
        </div>



      </FormModel>

    </>
  );
};

export default FormRegister;
