import React from 'react';
import { FieldValues, UseFormRegister, FieldErrors, useForm } from 'react-hook-form';

interface ChildComponentProps {
    register: UseFormRegister<FieldValues>;
    errors: FieldErrors<FieldValues>;
}

interface FormModelProps {
    children?: React.ReactElement<ChildComponentProps>[] | React.ReactElement<ChildComponentProps>;
    buttonText: string;
    onSubmit(data: any): void;
}

const FormModel: React.FC<FormModelProps> = ({ children, buttonText, onSubmit }) => {
    const { handleSubmit } = useForm();

    const submitForm = (data: any) => {
        onSubmit(data);
    };

    return (
        <form onSubmit={handleSubmit(submitForm)}>
            {children}
            <button type="submit">{buttonText}</button>
        </form>
    );
};

export default FormModel;
