import React from 'react';

interface InputProps {
    inputRef: any; // Você pode definir um tipo mais específico para inputRef se desejar
    type: string;
    placeholder: string;
    error?: string | null;
}

const InputModel: React.FC<InputProps> = ({ inputRef, type, placeholder, error }) => {
    return (
        <div className="my-10">
            <input
                ref={inputRef}
                type={type}
                placeholder={placeholder}
                className="p-5 w-full bg-slate-100 rounded-xl placeholder:text-lg"
            />
            {error && <div className="error">{error}</div>}
        </div>
    );
};

export default InputModel;
