import React, { useState } from 'react';

interface InputProps {
    placeholder: string;
    type: string;
}

const InputModel: React.FC<InputProps> = ({ placeholder, type }) => {
    const [error, setError] = useState<string | null>(null); // Estado para armazenar a mensagem de erro

    const handleBlur = (event: React.FocusEvent<HTMLInputElement>) => {
        const value = event.target.value.trim(); // Remove espaços em branco do início e do fim
        if (!value) {
            setError('Campo obrigatório'); // Define a mensagem de erro se o campo estiver vazio
        } else {
            setError(null); // Limpa a mensagem de erro se o campo estiver preenchido
        }
    };

    return (
        <div className="my-10">
            <input
                placeholder={placeholder}
                type={type}
                className="p-5 w-full bg-slate-100 rounded-xl placeholder:text-lg"
                onBlur={handleBlur} // Adiciona o evento onBlur para verificar o campo ao perder o foco
            />
            {error && <div className="error">{error}</div>} {/* Renderiza a div de erro apenas se houver um erro */}
        </div>
    );
};

export default InputModel;
