import React from 'react';
import { useForm } from 'react-hook-form';
import Logo from "../Logo/Logo";
import Button from "../Button/Button";

interface FormWrapperProps {
    heading: string;
    buttonText: string;
    onSubmit(data: any): void;
    children: React.ReactNode
}

const FormWrapper: React.FC<FormWrapperProps> = ({ children, heading, buttonText, onSubmit }) => {
    const { handleSubmit } = useForm();

    const submitForm = (data: any) => { 
        onSubmit(data);

    };

    return (
        <section className="flex flex-col items-center justify-center gap-3">
            <Logo />
            <section className="flex flex-col w-full md:w-3/4 lg:w-3/6 shadow-xl h-2/4 p-10 rounded">
                <h3 className="text-heading-blue-800 text-3xl font-bold">{heading}</h3>
                <form onSubmit={handleSubmit(submitForm)} className="w-full my-5" action="#" method="POST">
                    {children}
                    <Button text={buttonText} />
                </form>
            </section>
        </section>
    );
}

export default FormWrapper;
