import React from 'react';
import Logo from '../Logo/Logo';

interface FormWrapperProps {
    children?: React.ReactNode;
    heading: string;
    onSubmit: (formData: any) => void; // Função de retorno de chamada para lidar com a submissão do formulário
}

const FormWrapper: React.FC<FormWrapperProps> = ({ children, heading, onSubmit }) => {
    const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault(); // Impedir o comportamento padrão do formulário

     
        const formData = new FormData(event.currentTarget);
        const formValues: any = {}; // Objeto para armazenar os valores do formulário

   
        formData.forEach((value, key) => {
            formValues[key] = value;
        });

       
        onSubmit(formValues);
    };

    return (
        <section className="flex flex-col items-center justify-center gap-3">
            <Logo />

            <section className="flex flex-col w-full md:w-3/4 lg:w-3/6 shadow-xl h-2/4 p-10 rounded">
                <h3 className="text-heading-blue-800 text-3xl font-bold">{heading}</h3>

                <form onSubmit={handleSubmit}>
                    {children}
                    <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">
                        Submit
                    </button>
                </form>
            </section>
        </section>
    );
};

export default FormWrapper;
